import React, {useEffect, useState} from 'react';
import {ScrollView, Text, View} from 'react-native';
import axios from 'axios';
import PhotoDetail from './PhotoDetail';

const PhotoList = (props) => {

  /* const renderAlbums = () => {
    return photoset.map((photo) => (
      <PhotoDetail
        key={photo.title}
        title={photo.title}
        imageUrl={`https://farm${photo.farm}.staticflickr.com/${photo.server}/${photo.id}_${photo.secret}.jpg`}
      />
    ));
  } */

  const [photoset, setPhotoset] = useState(null);

  useEffect(() => {
    axios
    .get(
      'https://api.flickr.com/services/rest/?method=flickr.photosets.getList&api_key=6e8a597cb502b7b95dbd46a46e25db8d&user_id=137290658%40N08&format=json&nojsoncallback=1',
    )
    .then((response) => {
      console.log(response.data.photosets.photoset);
      setPhotoset(response.data.photosets.photoset);
    });
  }, [])

  if( !photoset ) {
    return(
      <View style={{flex: 1}}>
        <Text> Loading... </Text>
      </View>
    )
  } else {
    return (
      <View style={{flex: 1}}>
        <ScrollView>{ 
          photoset.map((photo) => (
            <PhotoDetail
              key={photo.title}
              title={photo.title}
              imageUrl={`https://farm${photo.farm}.staticflickr.com/${photo.server}/${photo.id}_${photo.secret}.jpg`}
            />
          )) }
        </ScrollView>
      </View>
    )   
  }


}

export default PhotoList;
